import React from 'react'

const Header = ({ name }) => {
    return(
        <h1>
            { name }
        </h1>
    )
}

const Content = ({ parts }) => {
    const partElements = parts.map(c =>
        <Part
            key={ c.id }
            part = { c }
        />
    );

    return(
        <ul>
            { partElements }
        </ul>
    )
}

const Part = ({ part }) => {
    return(
        <li>
            { part.name } { part.exercises }
        </li>
    )
}

const Course = ({ course }) => {
    return (
        <div>
            <Header
                name = { course.name }
            />
            <Content
                parts = { course.parts }
            />
            <Total
                parts = { course.parts }
            />
        </div>
    )
}

export default Course
